$(document).on('change', "#id_activite", function(e) {
    e.preventDefault();
     var url = "http://127.0.0.1:8000/home/";
     $.getJSON(url, {id_activite: $("#id_activite option:selected").val()}, function(raw) {
        var options = '<option value="">--------</option>';
        for (var i = 0; i < raw.length; i++) {
                options += '<option value="' + raw[i][0] +'">' + raw[i][1] + '</option>';

        }
		
        $("[name='trimestre']").html(options);
        $("#id_trimestre option:first").attr('selected', 'selected');
    });

});

